","keccak256":"0x3c07f43e60e099b3b157243b3152722e73b80eeb7985c2cd73712828d7f7da29","license":"MIT"},"contracts/MetaMultiSigWallet.sol":{"content":"// SPDX-License-Identifier: MIT

//  Off-chain signature gathering multisig that streams funds - @austingriffith
//
// started from 🏗 scaffold-eth - meta-multi-sig-wallet example https://github.com/austintgriffith/scaffold-eth/tree/meta-multi-sig
//    (off-chain signature based multi-sig)
//  added a very simple streaming mechanism where `onlySelf` can open a withdraw-based stream
//

pragma solidity >=0.8.0 <0.9.0;
// Not needed to be explicitly imported in Solidity 0.8.x
// pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "hardhat/console.sol";
import "./MultiSigFactory.sol";

contract MetaMultiSigWallet {
  using ECDSA for bytes32;

  event Deposit(address indexed sender, uint256 amount, uint256 balance);
  event ExecuteTransaction(address indexed owner, address payable to, uint256 value, bytes data, uint256 nonce, bytes32 hash, bytes result);
  event Owner(address indexed owner, bool added);
  mapping(address => bool) public isOwner;
  uint256 public signaturesRequired;
  uint256 public nonce;
  uint256 public chainId;
  IERC20 private _token;
  // factory
  MultiSigFactory private multiSigFactory;
  address[] public owners;

  constructor(
    uint256 _chainId,
    address[] memory _owners,
    uint256 _signaturesRequired,
    address _factory
  ) payable {
    require(_signaturesRequired > 0, "constructor: must be non-zero sigs required");
    multiSigFactory = MultiSigFactory(_factory);
    signaturesRequired = _signaturesRequired;
    for (uint256 i = 0; i < _owners.length; i++) {
      address owner = _owners[i];
      require(owner != address(0), "constructor: zero address");
      require(!isOwner[owner], "constructor: owner not unique");
      isOwner[owner] = true;
      emit Owner(owner, isOwner[owner]);
    }
    chainId = _chainId;
  }

  modifier onlySelf() {
    require(msg.sender == address(this), "Not Self");
    _;
  }

  function addSigner(address newSigner, uint256 newSignaturesRequired) public onlySelf {
    require(newSigner != address(0), "addSigner: zero address");
    require(!isOwner[newSigner], "addSigner: owner not unique");
    require(newSignaturesRequired > 0, "addSigner: must be non-zero sigs required");
    isOwner[newSigner] = true;
    signaturesRequired = newSignaturesRequired;
    emit Owner(newSigner, isOwner[newSigner]);
    multiSigFactory.emitOwners(address(this), owners, newSignaturesRequired);
  }

  function removeSigner(address oldSigner, uint256 newSignaturesRequired) public onlySelf {
    require(isOwner[oldSigner], "removeSigner: not owner");
    require(newSignaturesRequired > 0, "removeSigner: must be non-zero sigs required");
    isOwner[oldSigner] = false;
    signaturesRequired = newSignaturesRequired;
    emit Owner(oldSigner, isOwner[oldSigner]);
    multiSigFactory.emitOwners(address(this), owners, newSignaturesRequired);
  }

  function updateSignaturesRequired(uint256 newSignaturesRequired) public onlySelf {
    require(newSignaturesRequired > 0, "updateSignaturesRequired: must be non-zero sigs required");
    signaturesRequired = newSignaturesRequired;
  }

  function getTransactionHash(
    uint256 _nonce,
    address to,
    uint256 value,
    bytes memory data
  ) public view returns (bytes32) {
    return keccak256(abi.encodePacked(address(this), chainId, _nonce, to, value, data));
  }

  function executeTransaction(
    address payable to,
    uint256 value,
    bytes memory data,
    bytes[] memory signatures
  ) public returns (bytes memory) {
    require(isOwner[msg.sender], "executeTransaction: only owners can execute");
    bytes32 _hash = getTransactionHash(nonce, to, value, data);
    nonce++;
    uint256 validSignatures;
    address duplicateGuard;
    for (uint256 i = 0; i < signatures.length; i++) {
      address recovered = recover(_hash, signatures[i]);
      require(recovered > duplicateGuard, "executeTransaction: duplicate or unordered signatures");
      duplicateGuard = recovered;
      if (isOwner[recovered]) {
        validSignatures++;
      }
    }

    require(validSignatures >= signaturesRequired, "executeTransaction: not enough valid signatures");

    (bool success, bytes memory result) = to.call{ value: value }(data);
    console.log("multiSigFactory: ", address(multiSigFactory));
    require(success, "executeTransaction: tx failed");

    emit ExecuteTransaction(msg.sender, to, value, data, nonce - 1, _hash, result);
    return result;
  }

  function recover(bytes32 _hash, bytes memory _signature) public pure returns (address) {
    return _hash.toEthSignedMessageHash().recover(_signature);
  }

  receive() external payable {
    emit Deposit(msg.sender, msg.value, address(this).balance);
  }

  //
  //  new streaming stuff
  //

  event OpenStream(address indexed to, uint256 amount, uint256 frequency);
  event CloseStream(address indexed to);
  event Withdraw(address indexed to, uint256 amount, string reason);

  struct Stream {
    uint256 amount;
    uint256 frequency;
    uint256 last;
  }
  mapping(address => Stream) public streams;

  function streamWithdraw(uint256 amount, string memory reason) public {
    require(streams[msg.sender].amount > 0, "withdraw: no open stream");
    _streamWithdraw(payable(msg.sender), amount, reason);
  }

  function _streamWithdraw(
    address payable to,
    uint256 amount,
    string memory reason
  ) private {
    uint256 totalAmountCanWithdraw = streamBalance(to);
    require(totalAmountCanWithdraw >= amount, "withdraw: not enough");
    streams[to].last = streams[to].last + (((block.timestamp - streams[to].last) * amount) / totalAmountCanWithdraw);
    emit Withdraw(to, amount, reason);
    to.transfer(amount);
  }

  function streamBalance(address to) public view returns (uint256) {
    return (streams[to].amount * (block.timestamp - streams[to].last)) / streams[to].frequency;
  }

  function openStream(
    address to,
    uint256 amount,
    uint256 frequency
  ) public onlySelf {
    require(streams[to].amount == 0, "openStream: stream already open");
    require(amount > 0, "openStream: no amount");
    require(frequency > 0, "openStream: no frequency");

    streams[to].amount = amount;
    streams[to].frequency = frequency;
    streams[to].last = block.timestamp;

    emit OpenStream(to, amount, frequency);
  }

  function closeStream(address payable to) public onlySelf {
    require(streams[to].amount > 0, "closeStream: stream already closed");
    _streamWithdraw(to, streams[to].amount, "stream closed");
    delete streams[to];
    emit CloseStream(to);
  }
}
","keccak256":"0x3a84c69eb28d1d7451a760b3863f805f4c25b5a4c84e9a900df5464bf7a4c7fb","license":"MIT"},"contracts/MultiSigFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./MetaMultiSigWallet.sol";

contract MultiSigFactory {

  MetaMultiSigWallet[] public multiSigs;
  mapping(address => bool) existsMultiSig;

  event Create(uint256 indexed contractId, address indexed contractAddress, address creator, address[] owners, uint256 signaturesRequired);

  event Owners(address indexed contractAddress, address[] owners, uint256 indexed signaturesRequired);

  constructor() {}

  modifier onlyRegistered() {
    require(existsMultiSig[msg.sender], "caller not registered to use logger");
    _;
  }

  function emitOwners(
    address _contractAddress,
    address[] memory _owners,
    uint256 _signaturesRequired
  ) external onlyRegistered {
    emit Owners(_contractAddress, _owners, _signaturesRequired);
  }

  function create(
    uint256 _chainId,
    address[] memory _owners,
    uint256 _signaturesRequired
  ) public payable {
    uint256 id = numberOfMultiSigs();

    MetaMultiSigWallet multiSig = (new MetaMultiSigWallet){ value: msg.value }(_chainId, _owners, _signaturesRequired, address(this));
    multiSigs.push(multiSig);
    existsMultiSig[address(multiSig)] = true;

    emit Create(id, address(multiSig), msg.sender, _owners, _signaturesRequired);
    emit Owners(address(multiSig), _owners, _signaturesRequired);
  }

  function numberOfMultiSigs() public view returns (uint256) {
    return multiSigs.length;
  }

  function getMultiSig(uint256 _index)
    public
    view
    returns (
      address multiSigAddress,
      uint256 signaturesRequired,
      uint256 balance
    )
  {
    MetaMultiSigWallet multiSig = multiSigs[_index];
    return (address(multiSig), multiSig.signaturesRequired(), address(multiSig).balance);
  }
}
","keccak256":"0x5438ce12339ded4c49a3e03f69a50c72e7bd9ad062b127fbbe235daf2d39efef","license":"MIT"},"hardhat/console.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >= 0.4.22 <0.9.0;

library console {
	address constant CONSOLE_ADDRESS = address(0x000000000000000000636F6e736F6c652e6c6f67);

	function _sendLogPayload(bytes memory payload) private view {
		uint256 payloadLength = payload.length;
		address consoleAddress = CONSOLE_ADDRESS;
		assembly {
			let payloadStart := add(payload, 32)
			let r := staticcall(gas(), consoleAddress, payloadStart, payloadLength, 0, 0)
		}
